function getUsername(userId) {
  // Create line users json file
  // eg. [ "Uabcdefghijklmnopqrstuvwxyz0123456789": "太郎" }

  if (flow.get('line_users') && flow.get('line_users')[userId]) {
      return flow.get('line_users')[userId] + 'からLINE。';
  }

  return '';
}

if (typeof(msg.payload.data) === 'string') {
    msg.payload = msg.payload.data;
} else if (msg.payload.data[0]) {
  // Get LINE message sent from beebotte
  const event = msg.payload.data[0];

  if (event.type === 'message' && event.message.type === 'text') {
    const username = getUsername(event.source.userId);
    const text = username + event.message.text;

    if (event.source.groupId && 
        ! event.message.text.match(/^[　 ]/) &&
        ! event.message.text.match(/^\[IFTTT\][　 ]{2}/)) {
        return null;
    }

    msg.payload = text;
  } else if (event.type === 'message' && event.message.type === 'audio') {
    const audioUrl = 'https://api.line.me/v2/bot/message/' + event.message.id + '/content';
    flow.set("line_content_url", audioUrl);

    msg.payload = 'http://' + global.get('ip_address') + ':1880/linecontent';
    msg.audio = true;
  } else {
    node.warn('No text message.');
    return null;
  }
}

return msg;

